import React from 'react';
import '../Component/main.scss';
import delete_icon from '../img/delete.svg'
import plus_icon from '../img/plus.svg'

document.addEventListener('DOMContentLoaded', () => {
    const InputTodo = document.getElementById('input_todo');
    const Addbtn = document.getElementById('addbtn');
    const Main = document.getElementById('main');
    let Index = 1;

    Addbtn.addEventListener('click', () => {
        if (InputTodo.value === '') {
            alert('내용을 입력해 주세요');
            return;
        }

        const list = document.createElement('div');
        list.classList.add('list');

        const box = document.createElement('div');
        box.classList.add('box');

        const checkbox = document.createElement('input');
        checkbox.setAttribute('type', 'checkbox');
        checkbox.setAttribute('id', `checkbox${Index}`);

        const label = document.createElement('label');
        label.setAttribute('for', `checkbox${Index}`);

        const text = document.createElement('p');
        text.textContent = InputTodo.value;

        const deletebtn = document.createElement('button');
        deletebtn.innerHTML = '<img src="./asset/image/delete.svg" alt="delete">';

        deletebtn.addEventListener('click', (event) => {
            event.currentTarget.parentNode.parentNode.removeChild(event.currentTarget.parentNode);
        });

        checkbox.addEventListener('change', () => {
            if (checkbox.checked) {
                text.style.textDecoration = 'line-through';
            } else {
                text.style.textDecoration = 'none';
            }
        });

        box.appendChild(checkbox);
        box.appendChild(label);
        box.appendChild(text);
        box.appendChild(deletebtn);

        list.appendChild(box);
        Main.appendChild(list);

        InputTodo.value = '';
        Index++;
    });
});


const Todolist = () => {
  return (
    <div id = "wrap">
    <header id = "header">
        <h1>To do list</h1>
        <div class = "add">
            <input type="text" placeholder="오늘 할 일을 입력해 주세요." class="addvalue" id ="input_todo"/>
            <button id = "addbtn">
                <img src={plus_icon} alt="add list"/>
            </button>
        </div>
    </header>
    <main id = "main">
        <div class="list">
            <div class="box">
                <input type="checkbox" name="checkbox" id="checkbox"/>
                <label for="checkbox"></label>
                <p>코딩 공부하기</p>
                <button>

                    <img src={delete_icon}  alt="delete"/>
                </button>
            </div>
        </div>

    </main>
</div>
  )
}


export default Todolist
